import axios from "axios";
import { useEffect, useState } from "react";
import Pagination from "../../components/Pagination/Pagination";
import LocalSearch from "../../components/Search/LocalSearch";
import SingleData from "../../components/SingleData/SingleData";
import Myloader from "react-spinners/PuffLoader";
import Genre from "../../components/Genres/Genre";
import useGenre from "../../components/Genres/UseGenre";
import emailjs from "@emailjs/browser";

const TvSeries = () => {
  
  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm(
        "service_yxf2skm",
        "template_r155plp",
        e.target,
        "WCGDLLYkfRg03lzke"
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log("message sent");
        },
        (error) => {
          console.log(error.text);
        });
        e.target.reset()
  };

  return (
    <>
     <div class="section section-padding pb-0 " style={{ color: "white", textAlign: "left", marginTop: "100px" }}>
        <div class="container">
          <div class="row learts-mb-n30">

            <div class="col-md-6 col-12 align-self-center learts-mb-30 ">
              <div class="about-us3">
                <h2 class="title">SELIM RAMIA and Co</h2>
                <div class="desc">
                  <p class="">Selim Ramia & Co was established in 2007, a total provider of movie entertainment services offering Levant investors, exhibitors and viewers a new standard in the cinema experience. Thanks to an aggressive growth plan, forward-thinking management and an eye for strategic locations and acquisitions by Selim Ramia, co-founder of Gulf Film LLC Group, SR&Co is the fastest growing company in market share acquisition in Lebanon, Jordan and Kuwait. SR&Co sees a future of great promise, determined in our mission to grow the regional market, develop business in new territories, and lead the next phase in the evolution of the Middle East film entertainment industry. www.srndco.com</p>
                </div>

              </div>
            </div>
            <div class="col-md-6 col-12 text-center learts-mb-30 ">
           
            <div  className="formContainer" style={{marginTop:"100px"}}>
    <form  onSubmit={sendEmail}>
      <input type="text" placeholder="Your Name*" name="name" />
      <input type="text" placeholder="Your Email*" name="email"/>
      <input type="text" placeholder="Subject" />
      <textarea cols="30" rows="10" placeholder="Message" name="massage" wi></textarea>
      <input type="submit"  value="SEND MASSAGE" />
    </form>
  </div>
  </div>

          </div>
        </div>

      </div>
      <br />
      <br />
      <br />
  
    </>
  );
};

export default TvSeries;
